/**
 * Sets the shape for the avatar.
 *
 * Possible values are:
 * * (Default) `square`
 * * `rounded`
 * * `rectangle`
 * * `circle`
 *
 */
export declare type Shape = 'square' | 'rectangle' | 'rounded' | 'circle';
