/**
 * Specifies when the Тooltip will be rendered.
 *
 * The available options are:
 * - `hover`(default)&mdash;Tooltip is rendered when its `anchor` element is hovered.
 * - `click`&mdash;Tooltip is rendered when its `anchor` element is clicked.
 * - `none`&mdash;Tooltip is not rendered in `kendoTooltip` container.
 */
export declare type ShowOption = 'hover' | 'click' | 'none';
