/**
 * The scroll mode of the TreeList.
 *
 * The available values are:
 * - `none`
 * - `scrollable`
 *
 * @example
 * ```html-no-run
 * <kendo-treelist scrollable="scrollable"></kendo-treelist>
 * ```
 */
export declare type ScrollMode = 'none' | 'scrollable' | 'virtual';
